local light = require "light"

light.define [[
    typedef struct pos {
        int x;
        int y;
    } pos;
]]

function init ()
     component("pos", light.sizeof("pos"))
end

function update(rows)
    local data, count = column(rows)
    for i = 0, count - 1 do
        local speed = light.cast("pos", data)[i]
        speed.x = speed.x + 1
        print(speed.x, speed.y, count)
    end
end